style:
.custom-square-box {
    border: 1px solid #d3d3d3;
    display: flex;
    justify-content: center;
    align-items: center;
    box-shadow: 2px 2px 10px rgba(0,0,0,0.1);
    border-radius: 5px;
}

.square-box-content {
    text-align: center;
    padding: 4px;
    margin-top: 10px;
}

.custom-block-link {
    margin: 20px;
    transition: background-color 0.3s ease;
}

 .custom-block-link:hover {
    background-color: #ffa500;
    text-decoration: none;
}

.block-link {
    text-decoration: none;
}

.block-content h3 {
    margin: 0;
    font-size: 18px;
    font-weight: bold;
    text-decoration: none;
}

.block-content p {
    font-size: 14px;
    text-decoration: none;
}

